document.addEventListener("DOMContentLoaded",function(){const navbarHeight=document.querySelector(".navbar").offsetHeight;function scrollToElement(targetId){const targetElement=document.getElementById(targetId);if(targetElement){const targetPosition=targetElement.getBoundingClientRect().top+window.pageYOffset;const offsetPosition=targetPosition-navbarHeight;console.log(`ID target: ${targetId}`);console.log(`Posizione originale dell'elemento: ${targetPosition}`);console.log(`Altezza navbar: ${navbarHeight}`);console.log(`Posizione finale con offset: ${offsetPosition}`);window.scrollTo({top:offsetPosition,behavior:"smooth"});console.log(`Scroll effettuato verso: ${offsetPosition}`)}else{console.error(`Elemento con ID ${targetId} non trovato.`)}}document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener("click",function(e){e.preventDefault();const targetId=this.getAttribute("href").substring(1);console.log(`Cliccato su link con destinazione: ${targetId}`);scrollToElement(targetId)})})});document.addEventListener("DOMContentLoaded",function(){const navbarHeight=document.querySelector(".navbar").offsetHeight;function isMobileDevice(){return window.innerWidth<=768}function scrollToElement(targetId){const targetElement=document.getElementById(targetId);if(targetElement){const targetPosition=targetElement.getBoundingClientRect().top+window.pageYOffset;let offsetPosition=targetPosition-navbarHeight;if(isMobileDevice()&&targetId==="container1-1"){offsetPosition=targetPosition-navbarHeight+150}window.scrollTo({top:offsetPosition,behavior:"smooth"});console.log(`Scroll verso: ${targetId}, offset: ${offsetPosition}`)}else{console.error(`Elemento con ID ${targetId} non trovato.`)}}document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener("click",function(e){e.preventDefault();const targetId=this.getAttribute("href").substring(1);scrollToElement(targetId)})})});